import React from "react";
import "./Hero.css";
import hero from "../images/hero.svg";
import Button from "@material-ui/core/Button";

function Hero() {
  return (
    <div className='hero mt-5'>
      <div className='container'>
        <div className='row'>
          <div className='hero__left col-lg-5 col-md-6 order-1 order-md-0 text'>
            <h3 className='hero__caption text-uppercase'>Learn and practise</h3>
            <div className='hero__text'>
              <p>
                <span>Passaggrege </span>
                is the best online learning platform in Nigeria.<br/><br/>A broad
                platform for<span> Universities</span>,<span> Polytechnics</span>
                ,<span> Secondary Schools</span>,<span> Primary Schools</span>
              </p>
            </div>
            <Button>Explore</Button>
          </div>
          <div className='col-lg-7 col-md-6 order-0 order-md-1'>
            <img className='hero__image' src={hero} alt='hero image' />
          </div>
        </div>
      </div>
    </div>
  );
}
export default Hero;
