import React from 'react';
import "./Hero.css";
import hero from '../images/hero.svg';
im


function Hero () {
    return (
      <div className='hero mt-5'>
        <div className='container'>
          <div className='row'>
            <div className='col-lg-5 col-md-6 order-1 order-md-0'>
              <h3 className='hero__caption text-uppercase'>Learn and pass</h3>
              <div className='hero__text'>
                Business, Technology and Creative Skills taught by industry
                experts. Explore a wide range of skills with our professional
                tutorials.
              </div>
              <Button></Button>
            </div>
            <div className='col-lg-7 col-md-6 order-0 order-md-1'>
              <img className='hero__image' src={hero} alt='hero image' />
            </div>
          </div>
        </div>
      </div>
    );
}
export default Hero;
