import React from 'react';
import "./Hero.css";
import hero from '../images/hero.svg';


function Hero () {
    return (
      <div className='hero mt-5'>
        <div className='container'>
          <div className='row'>
            <div className='col-sm-12 col-md-6'>
              <div className="hero__text align-items">
                <h3>Learn and pass all your exams on passaggregate, No carry over, No failure</h3>
              </div>
            </div>
            <div className='col-sm-12 col-md-6'>
              <img src={hero} alt="hero image"/>
            </div>
          </div>
        </div>
      </div>
    );
}
export default Hero;
