import React from "react";
import "./Hero.css";
import hero from "../images/hero.svg";
import Button from "@material-ui/core/Button";

function Hero() {
  return (
    <div className='hero container'>
        <div className='row d-flex align-items-center text-md-left text-center'>
          <div className='col-lg-5 col-md-6 order-1 order-md-0'>
            <div className='hero__left'>
              <h2 className='hero__caption text-uppercase mb-4'>
                Learn and practise
              </h2>
              <p className='hero__text mb-4'>
                <b className="text-danger">Passaggregate </b>
                is the best online learning platform in Nigeria.
                <br /><br/>
                A broad platform for<span> Universities</span>,
                <span> Polytechnics</span>,<span> Secondary Schools</span> and
                <span> Primary Schools</span>
              </p>
              <Button>Explore</Button>
            </div>
          </div>
          <div className='col-lg-7 col-md-6 order-0 order-md-1'>
            <img className='hero__image' src={hero} alt='hero image' />
          </div>
        </div>
      </div>
  );
}
export default Hero;
