import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import heroBg from "../images/hero-background.svg";
import heroRight from "../images/hero.svg";

const useStyles = makeStyles((theme) => ({
  root: {
    paddingTop: theme.spacing(6),
    backgroundImage: `url(${heroBg})`,
    height: 500,
  },
  order: {
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column-reverse",
    },
  },
}));

function HeroSection() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid container className={classes.order}>
        <Grid item xs={12} md={5}>
          <Box
            display='flex'
            justifyContent='center'
            alignItems='center'
            style={{ height: "100%" }}
          >
            <Typography color='secondary' variant='h5'>
              Passaggregate is the best online learning plat-form in Nigeria.
              <span>
                A broad platform for Universities, Polytechnics, Secondary
                Schools and Primary Schools.
              </span>
            </Typography>
            <Button>Explore</Button>
          </Box>
        </Grid>

        <Grid item xs={12} md={7}>
          <img src={heroRight} height='500px' width='100%' alt='hero image' />
        </Grid>
      </Grid>
    </div>
  );
}

export default HeroSection;
